SELECT name, versioning_enabled, server_side_encryption_configuration, block_public_acls, block_public_policy, ignore_public_acls, restrict_public_buckets, bucket_policy_is_public, acl, tags_src as tags FROM aws_s3_bucket WHERE bucket_policy_is_public = true OR block_public_acls = false OR block_public_policy = false OR server_side_encryption_configuration IS NULL OR acl->>'Owner' != acl->>'Grantee';
